import React, { Component } from "react";
import { Card, Space, Spin } from "antd";
import Meta from "antd/lib/card/Meta";
import { LoadingOutlined, CloseOutlined } from "@ant-design/icons";

import { fetchMovies, fetchMovieDetails } from "../redux/movies/movies.actions";
import { connect } from "react-redux";
import { createStructuredSelector } from "reselect";
import {
  selectMovieData,
  selectIsLoading,
} from "../redux/movies/movies.selector";
import MovieDetails from "./moviedetails.component";

class List extends Component {
  constructor(props) {
    super(props);
    this.state = {
      keynum: this.props.keynum,
      query: "",
      viewDetails: false,
      movieId: null,
    };
    this.handleViewMovie = this.handleViewMovie.bind(this);
  }

  componentDidMount() {
    switch (this.props.keynum) {
      case "1":
        this.props.fetchMovies("popular");
        break;
      case "2":
        this.props.fetchMovies("upcoming");
        break;
      case "3":
        this.props.fetchMovies("trending");
        break;
      case "4":
        this.props.fetchMovies("search", this.props.query);
        break;
      default:
        break;
    }
  }

  componentWillReceiveProps(props) {
    if (props.query !== this.props.query) {
      this.props.fetchMovies("search", props.query);
    }
  }

  handleViewMovie(id) {
    this.props.fetchMovieDetails(id);
    this.setState({ viewDetails: true, movieId: id });
  }

  render() {
    const antIcon = <LoadingOutlined style={{ fontSize: 24 }} spin />;

    const { loading, movies } = this.props;

    return this.state.viewDetails ? (
      <div>
        <CloseOutlined
          onClick={() => {
            this.setState({ viewDetails: false });
          }}
          style={{
            fontSize: 20,
            display: "flex",
            justifyContent: "flex-end",
            marginRight: 100,
            marginTop: 5,
          }}
        />
        <MovieDetails id={this.state.movieId} />
      </div>
    ) : loading ? (
      <Spin indicator={antIcon} />
    ) : (
      <Space
        size="large"
        style={{ display: "flex", flexWrap: "wrap", justifyContent: "center" }}
      >
        {movies.map((movie) => (
          <Card
            key={movie.id}
            hoverable
            style={{ width: 240, marginBottom: 25 }}
            onClick={() => this.handleViewMovie(movie.id)}
            cover={
              <img
                alt="poster"
                src={
                  !movie.poster_path
                    ? "data:image/png;base64,"
                    : `https://image.tmdb.org/t/p/w500/${movie.poster_path}`
                }
              />
            }
          >
            <Meta
              title={movie.title}
              description={"Click to view more details"}
            />
          </Card>
        ))}
      </Space>
    );
  }
}

const mapStateToProps = createStructuredSelector({
  movies: selectMovieData,
  loading: selectIsLoading,
});

const mapDispatchToProps = (dispatch) => ({
  fetchMovies: (category, query) => dispatch(fetchMovies(category, query)),
  fetchMovieDetails: (movieId) => dispatch(fetchMovieDetails(movieId)),
});

export default connect(mapStateToProps, mapDispatchToProps)(List);
